import React, { useState } from 'react';
import axios from 'axios';

const App = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  const [loginId, setLoginId] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Send credentials to the backend for validation
    try {
      const response = await axios.post('http://localhost:5000/validate-credentials', {
        email,
        password
      });

      if (response.data.success) {
        setLoginStatus('Login successful!');
        // Trigger the login request
        handleLoginRequest();
      } else {
        setLoginStatus('Login failed. Check your credentials.');
      }
    } catch (error) {
      console.error('Error during login request:', error);
      setLoginStatus('An error occurred while logging in.');
    }
  };

  const handleLoginRequest = async () => {
    try {
      const response = await axios.post('http://localhost:5000/login-request');
      if (response.data.loginId) {
        setLoginId(response.data.loginId);
        setLoginStatus('Login request initiated, waiting for approval on the mobile app...');
      }
    } catch (error) {
      console.error('Error creating login request:', error);
      setLoginStatus('An error occurred while initiating the login request.');
    }
  };

  return (
    <div>
      <h1>PC Login</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Email:
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </label>
        <br />
        <label>
          Password:
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">Login</button>
      </form>
      <p>{loginStatus}</p>
      {loginId && <p>Login ID: {loginId}</p>}
    </div>
  );
};

export default App;
