// src/App.jsx
import React, { useState } from 'react';
import axios from 'axios'; // Ensure axios is installed for API calls
import './App.css';

function App() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  const [authMessage, setAuthMessage] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      // Send login request to the backend
      const response = await axios.post('http://localhost:5000/login-request', {
        email,
        password,
      });

      // Check if login request was successful
      if (response.data.success) {
        setLoginStatus('Login request sent to mobile. Please authenticate.');

        // You can handle any response logic here, such as triggering notifications for the mobile app

        // Example: You may want to set a variable to wait for mobile app authentication confirmation
        // For now, simulate a successful login
        setAuthMessage('Please authenticate on your mobile app');
      } else {
        setLoginStatus('Login failed. Check your credentials.');
      }
    } catch (error) {
      console.error('Login request failed', error);
      setLoginStatus('Login request failed. Try again later.');
    }
  };

  return (
    <div className="App">
      <h1>PC Login</h1>
      <form onSubmit={handleLogin}>
        <div>
          <label>Email</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Login</button>
      </form>

      {loginStatus && <p>{loginStatus}</p>}
      {authMessage && <p>{authMessage}</p>}
    </div>
  );
}

export default App;
